mapper.select:
select

from xy_xmf_order
where 1=1

    and OPEN_ID = #{openId,jdbcType=VARCHAR}


    and STATUS =#{status,jdbcType=INTEGER}


    and TYPE =#{type,jdbcType=VARCHAR}


    and EXTEND3 =#{extend3,jdbcType=VARCHAR}


    and STR_TO_DATE (
    CREATE_TIME,
    '%Y-%m-%d %T'
    ) BETWEEN STR_TO_DATE (
    #{startTime,jdbcType=VARCHAR},
    '%Y-%m-%d %T'
    )
    AND STR_TO_DATE (
    #{endTime,jdbcType=VARCHAR},
    '%Y-%m-%d %T'
    )

order by ORDER_ID DESC

select count(1) from xy_xmf_order where 1=1
and OPEN_ID = #{openId,jdbcType=VARCHAR}
and STATUS =#{status,jdbcType=INTEGER}
and STR_TO_DATE ( CREATE_TIME, '%Y-%m-%d %T' ) BETWEEN STR_TO_DATE ( #{startTime,jdbcType=VARCHAR}, '%Y-%m-%d %T' ) AND STR_TO_DATE ( #{endTime,jdbcType=VARCHAR}, '%Y-%m-%d %T' )
