mapper.select:
select

from yy_order_ks
where 1=1

    and PERSON_ID = #{personId,jdbcType=VARCHAR}


    and MOBILE = #{mobile,jdbcType=VARCHAR}


    and SOURCE = #{source,jdbcType=INTEGER}


    and STATUS =#{status,jdbcType=INTEGER}


    and EXTEND1 =#{extend1,jdbcType=INTEGER}


    and STR_TO_DATE (
    CREATE_TIME,
    '%Y-%m-%d %T'
    ) BETWEEN STR_TO_DATE (
    #{startTime,jdbcType=VARCHAR},
    '%Y-%m-%d %T'
    )
    AND STR_TO_DATE (
    #{endTime,jdbcType=VARCHAR},
    '%Y-%m-%d %T'
    )

order by ORDER_ID DESC limit #{bigenPage,jdbcType=INTEGER},#{endPage,jdbcType=INTEGER}

select
count(1)
from yy_order_ks
where 1 =1

    and PERSON_ID = #{personId,jdbcType=VARCHAR}


    and MOBILE = #{mobile,jdbcType=VARCHAR}


    and SOURCE = #{source,jdbcType=INTEGER}


    and STATUS =#{status,jdbcType=INTEGER}


    and STR_TO_DATE (
    CREATE_TIME,
    '%Y-%m-%d %T'
    ) BETWEEN STR_TO_DATE (
    #{startTime,jdbcType=VARCHAR},
    '%Y-%m-%d %T'
    )
    AND STR_TO_DATE (
    #{endTime,jdbcType=VARCHAR},
    '%Y-%m-%d %T'
    )
